import React from "react";
import WorkerDetails from "./WorkerDetails";

class WorkerSearch extends React.Components{

    constructor(props) {
        super(props);
        this.state = {
            enteredText: null,
            result: null
        }
        this.changeText = this.changeText.bind(this);
        this.testText = this.testText.bind(this);
    }

    
    changeText(e) {
        var update = e.target.value;
        this.setState({
            
            enteredText: update
        })
        
    }


     testText() {
         console.log('enteredtext', this.state.enteredText)         
       var result = WorkerDetails.filter(function (workerDetail) {
           return workerDetail.areapincode === this.state.enteredText;
        });
         var update = result;
         console.log("result", result);
         this.setState({
            result: update
        })
    }

    
    render() {
        return (
            <div>
                {console.log("entered text", this.state.enteredText)}

                <input
                    type="number"
                    id="workersearchinput"
                    placeholder="Enter Pincode"
                    onChange={this.changeText}
                />
                <select name="jobs" value="">
                    {" "}
                    <option value="select"> --select--</option>
                    <option value="maid">MAID</option>
                    <option value="cook">COOK</option>
                    <option value="nanny">NANNY</option>
                </select>
                <button onClick={this.testText}>Find</button>

            </div>
        );       
    }
  
}

export default WorkerSearch;
