import React from "react";

class FillForm extends React.Component {
  constructor() {
    super();
      this.handleSubmit = this.handleSubmit.bind(this);
      this.state = {
          username: ",",
          email: "",
          mobile: "",
          password: ""    
      }
  }

//   formDetails(username, email, mobile, password) {
//     return {
//       username: username,
//       email: email,
//       mobile: mobile,
//       password: password
//     };
//   }

  handleSubmit(event) {
      event.preventDefault();
      console.log('event', event.target);
      const name = event.target.name;
      const value = event.target.value;
      this.setState({
          [name]: value
    });
      console.log('event', this.state);

  }

  render() {
    return <div>
        <form className="container" onSubmit={this.handleSubmit}>
          <h1>USER</h1>
          <input id="username" name="username" placeholder="Name" onChange={this.handleSubmit} type="text" />

          <input id="email" name="email" placeholder="EMAIL" onChange={this.handleSubmit} type="email" />

          <input id="mobile" name="mobile" placeholder="Mobile" onChange={this.handleSubmit} type="text" />

          <input id="password" name="password" placeholder="Password" onChange={this.handleSubmit} type="text" />

          <input type="submit" id="register" value="REGISTER" />
        </form>
      </div>;
  }
}

export default FillForm;
