import React from "react";
import WorkerDetails from "./WorkerDetails";

const WorkerSearch = props => {

    var enteredText = null;
    function changeText(e) {
        enteredText = e.target.value;
    }

    var result = function filterWorkerDetails() {
        WorkerDetails.filter(function (workerDetail) {
            return workerDetail.areapincode === enteredText;
        });
    }
  return (
      <div>
          {console.log('entered text', enteredText)}
      <form>
        <input
                  type="number"
                  id="workersearchinput"
                  placeholder="Enter Pincode"
                  onChange={changeText}
        />
        <select name="jobs" value="">
          {" "}
          <option value="select"> --select--</option>
          <option value="maid">MAID</option>
          <option value="cook">COOK</option>
          <option value="nanny">NANNY</option>
              </select>
              <button onClick={filterWorkerDetails}>Find</button>
      </form>
    </div>
  );
};

export default WorkerSearch;
